#include <iostream>
#include <vector>

using std::vector;
using std::string;
using std::cout;

vector<string> KmerComposition(string Text, int k) {
    vector<string> kmers;
    for (auto iter = Text.begin(); iter <= Text.end() - k; ++iter) {
        kmers.emplace_back(iter, iter + k);
    }
    return kmers;
}


int main() {
    string s{
            ""};
    auto res = KmerComposition(s, 100);
    for (const auto &kmer: res) {
        cout << kmer << " ";
    }

    return 0;
}
